<script>
  import { createEventDispatcher } from 'svelte'
  import ProgressBar from 'components/ProgressBar.svelte'

  const dispatch = createEventDispatcher()

  let totalSeconds = 20
  let secondsLeft = totalSeconds

  let isRunning = false

  $: secondsElapsed = totalSeconds - secondsLeft
  $: value = secondsElapsed * 100 / totalSeconds

  const start = () => {
    isRunning = true
    const interval = setInterval(() => {
      secondsLeft--
      if (secondsLeft <=0) {
        clearInterval(interval)
        isRunning = false
        secondsLeft = totalSeconds
        dispatch('done', `you washed your hands for ${totalSeconds} seconds`)
      }
    }, 1000)
  }

</script>

<style>
  h2 {
    margin: 0;
  }
  .start {
    background-color: rgb(154, 73, 73);
    width: 100%;
  }
  .start[disabled] {
    background-color: rgb(194, 194, 194);
    cursor: not-allowed;
  }
</style>

<div bp="grid">
  <h2 bp="offset-5@md 4@md 12@sm">
    Seconds Left: { secondsLeft }
  </h2>
</div>

<ProgressBar {value}/>

<div bp="grid">
  <button on:click={start} disabled={isRunning} bp="offset-5@md 4@md 12@sm" class="start">Start</button>
</div>
